// TODO: Use this to color our human so you can maybe
// TODO: tell if you created them.
const color = ['red', 'blue', 'green'][Math.floor(Math.random() * 2)];
let humans = [];

function clearHumans(event) {
  if (event.stopPropagation) {
    event.stopPropagation();
  }
  const options = {
      method: 'DELETE',
  };
  fetch("/humans", options);

  document.querySelectorAll('.human').forEach(function(a){
    a.remove()
  });
  humans = [];
}

poseKeys = Object.keys(poses);
document.addEventListener("click", async function(evnt) {
    let pose = poseKeys[Math.floor(Math.random() * 3)];
    let point = { id: 0, x: evnt.clientX, y: evnt.clientY, pose: +pose };

    const options = {
        method: 'POST',
        body: JSON.stringify(point),
        headers: {
            'Content-Type': 'application/json'
        }
    };

    // Send our POST request
    let res = await fetch("/humans", options);
    let resp = await res.json();

    humans = resp.humans;

    let human = humans.pop();
    drawPerson([human.id, human.x, human.y, human.pose]);
});

function drawPerson(human) {
  const personSVG = poses[human[3]];

  var newPerson = document.createElement('svg');
  newPerson.innerHTML = personSVG;
  newPerson.className = 'human';

  newPerson.id = human[0];
  newPerson.style.left = human[1]+"px";
  newPerson.style.top = human[2]+"px";

  document.body.appendChild( newPerson );
}

// Handle our random talking
function toggleTalk() {
  var timeArray = new Array(20000, 3000, 1500, 2500, 2000, 3000, 10000, 15000);

  if ( humans.length ) {
    let human = humans[Math.floor(Math.random() * ( humans.length - 1 ))];
    // TODO Add some words to [human.x, human.y] location
  }

  clearInterval(timer);
  timer = setInterval(toggleTalk, timeArray[Math.floor(Math.random()* ( timeArray.length - 1 ))]);
}

var timer = setInterval(toggleTalk, 1000);
